const fieldsToClean = ['subjectLine', 'emailBody', 'leadSummary'];
let output = {};

for (const field of fieldsToClean) {
  const value = $json[field] || '';
  output[field] = value.replace(/—/g, '-');
}

return {
  json: output
};
