const data = $json.data;
// Function to extract text from HTML while removing unnecessary spaces
function extractTextFromHTML(html) {
  if (typeof html !== 'string') {
    return '';
  }
  return html
    .replace(/<script[\s\S]*?>[\s\S]*?<\/script>/gi, '')
    .replace(/<style[\s\S]*?>[\s\S]*?<\/style>/gi, '')
    .replace(/<[^>]+>/g, '')
    .replace(/\s+/g, ' ')
    .trim();
}
let extractedText = extractTextFromHTML(data);
// Check if extracted text is empty and return "no content" if so
return { text: extractedText || "no content" };

