const keywords = [
  // Carriers
  "UnitedHealthcare", "UHC", "Cigna", "Aetna", "Blue Cross", "BCBS", "Guardian", "MetLife", 
  "Humana", "Anthem", "Kaiser", "CBIZ", "Lincoln Financial", "Sun Life", "Hartford", 
  "Assurant", "Ameritas",

  // Vendors
  "Gusto", "Zenefits", "Rippling", "Ease", "Namely", "Employee Navigator", "Paychex", 
  "ADP", "Benefitfocus", "Workday", "BambooHR", "TriNet", "Justworks", "Insperity", 
  "UKG", "Ceridian", "Paylocity", "Paycor", "Bswift", "Businessolver",

  // Industry descriptors
  "carrier", "insurance provider", "benefits platform", "HRIS", "HR platform", "payroll provider",

  // General categories
  "retired", "stay at home", "homemaker", "freelancer", "self-employed"
];

for (const item of items) {
  const title = (item.json.title || "").toLowerCase();
  const description = (item.json.description || "").toLowerCase();

  const matchedKeyword = keywords.find(keyword => {
    const lowerKeyword = keyword.toLowerCase();
    return title.includes(lowerKeyword) || description.includes(lowerKeyword);
  });

  if (matchedKeyword) {
    throw new Error(`Disqualified due to keyword match: "${matchedKeyword}" in "${item.json.title}"`);
  }
}

return items;

