// Enhanced n8n Logo Detector with improved scoring and class-based detection
const html = $input.first().json.html;
const baseUrl = $input.first().json.url || $input.first().json.baseUrl;

// Helper functions
function resolveUrl(url, base) {
  if (!url) return null;
  try {
    if (url.startsWith('http://') || url.startsWith('https://')) return url;
    if (url.startsWith('//')) return 'https:' + url;
    if (url.startsWith('/')) {
      const baseUrlObj = new URL(base);
      return baseUrlObj.origin + url;
    }
    return new URL(url, base).href;
  } catch (e) {
    return null;
  }
}

// FIXED: More robust attribute extraction
function extractAttribute(htmlTag, attribute) {
  const patterns = [
    new RegExp(`${attribute}\\s*=\\s*["']([^"']*?)["']`, 'i'),
    new RegExp(`${attribute}\\s*=\\s*([^\\s>]+)`, 'i'), // Unquoted attributes
    new RegExp(`${attribute}\\s*=\\s*"([^"]*?)"`, 'i'),
    new RegExp(`${attribute}\\s*=\\s*'([^']*?)'`, 'i')
  ];

  for (const pattern of patterns) {
    const match = htmlTag.match(pattern);
    if (match) return match[1];
  }
  return '';
}

// ENHANCED: Better scoring system that catches class-based logos
function calculateLogoScore(src, alt, className, id, method, position) {
  let score = 0.1; // Lower base score to be more inclusive

  const filename = src.split('/').pop().toLowerCase();
  const path = src.toLowerCase();
  const altLower = alt.toLowerCase();
  const classLower = className.toLowerCase();
  const idLower = id.toLowerCase();

  // STRONG logo indicators (high confidence)
  if (filename.includes('logo')) score += 0.6;
  if (altLower.includes('logo')) score += 0.4;
  if (classLower.includes('logo') || idLower.includes('logo')) score += 0.5;

  // CRITICAL FIX: Detect common logo classes
  const logoClasses = ['logo-main', 'site-logo', 'main-logo', 'header-logo', 'brand-logo', 'company-logo'];
  const stickyClasses = ['logo-sticky', 'sticky-logo'];
  const mobileClasses = ['logo-mobile', 'mobile-logo'];

  if (logoClasses.some(cls => classLower.includes(cls))) score += 0.7;
  if (stickyClasses.some(cls => classLower.includes(cls))) score += 0.6; // Sticky logos are main logos
  if (mobileClasses.some(cls => classLower.includes(cls))) score += 0.6; // Mobile logos are main logos

  // Brand-related terms
  if (filename.includes('brand')) score += 0.4;
  if (filename.includes('site-logo')) score += 0.6;
  if (filename.includes('cropped') && filename.includes('logo')) score += 0.4;

  // Header/navigation context
  if (filename.includes('header')) score += 0.3;
  if (filename.includes('nav')) score += 0.2;
  if (classLower.includes('header') || classLower.includes('nav')) score += 0.2;

  // WordPress specific patterns
  if (path.includes('wp-content/uploads') && filename.includes('logo')) score += 0.3;
  if (filename.includes('cropped-')) score += 0.2;

  // File extension preferences
  if (src.toLowerCase().endsWith('.png')) score += 0.2;
  if (src.toLowerCase().endsWith('.svg')) score += 0.25;

  // Position bonus (earlier = more likely to be main logo)
  if (position < 100) score += 0.2;   // Very early
  if (position < 500) score += 0.15;  // Early
  if (position < 1000) score += 0.1;  // Moderately early

  // PENALTIES for non-logo patterns
  const uuidPattern = /[a-f0-9]{8}-[a-f0-9]{4}-[a-f0-9]{4}-[a-f0-9]{4}-[a-f0-9]{12}/i;
  if (uuidPattern.test(filename) && !filename.includes('logo')) score -= 0.4;

  const stockPatterns = ['unsplash', 'shutterstock', 'getty', 'stock'];
  if (stockPatterns.some(pattern => src.toLowerCase().includes(pattern))) score -= 0.5;

  // Penalty for obvious non-logos
  const nonLogoPatterns = ['dummy', 'placeholder', 'icon-', 'button-', 'bg-', 'background'];
  if (nonLogoPatterns.some(pattern => filename.includes(pattern))) score -= 0.3;

  // Method-based adjustments
  if (method === 'json-ld') score += 0.2;
  if (method === 'header-section') score += 0.1;

  return Math.max(0, Math.min(1, score));
}

const logoUrls = [];

// FIXED: More comprehensive image extraction
const imgPatterns = [
  /<img[^>]*>/gi,
  /<image[^>]*>/gi,
  /<source[^>]*>/gi
];

let allMatches = [];
imgPatterns.forEach(pattern => {
  const matches = html.match(pattern) || [];
  allMatches = allMatches.concat(matches);
});

// Also check for CSS background images
const stylePattern = /style\s*=\s*["'][^"']*background[^"']*["']/gi;
const styleMatches = html.match(stylePattern) || [];
styleMatches.forEach(styleMatch => {
  const urlMatch = styleMatch.match(/url\(['"]?([^'"()]+)['"]?\)/i);
  if (urlMatch) {
    allMatches.push(`<div style="background-image: url('${urlMatch[1]}')" data-bg-logo="true">`);
  }
});

// Process all matches
allMatches.forEach((imgTag, index) => {
  let src = extractAttribute(imgTag, 'src') || 
           extractAttribute(imgTag, 'data-src') || 
           extractAttribute(imgTag, 'data-lazy-src') ||
           extractAttribute(imgTag, 'srcset')?.split(',')[0]?.trim()?.split(' ')[0];

  // Handle background images
  if (imgTag.includes('data-bg-logo')) {
    const urlMatch = imgTag.match(/url\(['"]?([^'"()]+)['"]?\)/i);
    if (urlMatch) src = urlMatch[1];
  }

  const alt = extractAttribute(imgTag, 'alt');
  const className = extractAttribute(imgTag, 'class');
  const id = extractAttribute(imgTag, 'id');

  // CRITICAL: Don't skip images without src - they might be important!
  // Instead, process ALL images and log what we're missing
  if (src || imgTag.includes('src=')) {
    const resolvedUrl = src ? resolveUrl(src, baseUrl) : null;

    // Even if URL resolution fails, log the attempt for debugging
    const confidence = src ? calculateLogoScore(src, alt, className, id, 'enhanced-scan', index) : 0;

    const logoCandidate = {
      url: resolvedUrl || 'FAILED_TO_RESOLVE',
      alt: alt,
      className: className,
      id: id,
      method: 'enhanced-scan',
      confidence: confidence,
      filename: src ? src.split('/').pop() : 'NO_SRC',
      fileExtension: src ? src.split('.').pop().toLowerCase() : 'unknown',
      position: index,
      rawSrc: src || 'MISSING',
      rawTag: imgTag.substring(0, 200), // First 200 chars for debugging
      aiAnalysis: {
        hasLogoInFilename: src ? src.toLowerCase().includes('logo') : false,
        hasLogoInAlt: alt.toLowerCase().includes('logo'),
        hasLogoInClass: className.toLowerCase().includes('logo'),
        isPng: src ? src.toLowerCase().endsWith('.png') : false,
        isSvg: src ? src.toLowerCase().endsWith('.svg') : false,
        isWordPress: src ? src.includes('wp-content') : false,
        isCropped: src ? src.includes('cropped-') : false,
        isLikelyUUID: src ? /[a-f0-9]{8}-[a-f0-9]{4}/.test(src) : false,
        isStockPhoto: src ? ['unsplash', 'shutterstock', 'getty', 'stock'].some(p => src.toLowerCase().includes(p)) : false,
        hasMainLogoClass: ['logo-main', 'main-logo', 'site-logo'].some(cls => className.toLowerCase().includes(cls)),
        hasStickyLogoClass: ['logo-sticky', 'sticky-logo'].some(cls => className.toLowerCase().includes(cls)),
        hasMobileLogoClass: ['logo-mobile', 'mobile-logo'].some(cls => className.toLowerCase().includes(cls))
      }
    };

    // Only add to final results if we have a valid URL, but keep all for debugging
    if (resolvedUrl) {
      logoUrls.push(logoCandidate);
    }
  }
});

// Remove duplicates and sort by confidence
const uniqueLogos = [];
const seenUrls = new Set();

logoUrls.forEach(logo => {
  if (!seenUrls.has(logo.url)) {
    seenUrls.add(logo.url);
    uniqueLogos.push(logo);
  }
});

uniqueLogos.sort((a, b) => b.confidence - a.confidence);

// FIXED: Lower threshold to catch more candidates
const topCandidates = uniqueLogos.filter(logo => logo.confidence > 0.05).slice(0, 15);

// Enhanced AI decision logic
function shouldSendToAI() {
  if (topCandidates.length === 0) return false;

  const bestLogo = topCandidates[0];

  // If we have a very high confidence logo with clear indicators, don't need AI
  if (bestLogo.confidence >= 0.9 && 
      (bestLogo.aiAnalysis.hasMainLogoClass || 
       (bestLogo.aiAnalysis.hasLogoInFilename && bestLogo.aiAnalysis.hasLogoInClass)) &&
      !bestLogo.aiAnalysis.isStockPhoto && 
      !bestLogo.aiAnalysis.isLikelyUUID) {
    return false;
  }

  // If we have multiple high-confidence results that are close, use AI
  const highConfidenceResults = topCandidates.filter(logo => logo.confidence >= 0.5);
  if (highConfidenceResults.length >= 2) {
    const confidenceRange = highConfidenceResults[0].confidence - highConfidenceResults[1].confidence;
    if (confidenceRange <= 0.2) return true;
  }

  // If best logo confidence is moderate, use AI
  return bestLogo.confidence < 0.8;
}

// Generate AI prompt
function generateAIPrompt() {
  if (!shouldSendToAI() || topCandidates.length === 0) return null;

  const promptText = `Analyze these ${topCandidates.length} logo candidates for "${baseUrl}" and select the main company/site logo:

${topCandidates.map((logo, i) => 
  `${i+1}. ${logo.url}
     Alt: "${logo.alt}"
     Class: "${logo.className}"
     Score: ${logo.confidence.toFixed(2)}
     Position: ${logo.position}
     Main logo class: ${logo.aiAnalysis.hasMainLogoClass}
     Has 'logo' in filename: ${logo.aiAnalysis.hasLogoInFilename}
     Has 'logo' in class: ${logo.aiAnalysis.hasLogoInClass}`
).join('\n\n')}

Return just the number (1-${topCandidates.length}) of the best main company logo. Choose the one that represents the primary brand/company, not partner logos or secondary graphics.`;

  return promptText;
}

// Enhanced debugging info with raw image tags
const debugInfo = {
  totalImagesFound: allMatches.length,
  rawImageTags: allMatches.slice(0, 10).map((tag, i) => ({
    index: i,
    tag: tag.substring(0, 150) + (tag.length > 150 ? '...' : ''),
    src: extractAttribute(tag, 'src'),
    class: extractAttribute(tag, 'class'),
    alt: extractAttribute(tag, 'alt')
  })),
  allLogoCandidates: logoUrls.map(logo => ({
    url: logo.url,
    confidence: logo.confidence,
    filename: logo.filename,
    className: logo.className,
    hasLogoClass: logo.aiAnalysis.hasLogoInClass,
    hasMainLogoClass: logo.aiAnalysis.hasMainLogoClass,
    rawSrc: logo.rawSrc
  })),
  topScores: topCandidates.slice(0, 5).map(logo => ({
    url: logo.url,
    confidence: logo.confidence,
    filename: logo.filename,
    className: logo.className
  })),
  missedInnovoLogo: logoUrls.find(logo => logo.filename && logo.filename.includes('innovo-logo')),
  searchForInnovo: {
    foundInRawTags: allMatches.filter(tag => tag.toLowerCase().includes('innovo-logo')),
    foundInSrcAttributes: allMatches.map(tag => extractAttribute(tag, 'src')).filter(src => src && src.includes('innovo-logo'))
  }
};

return {
  logos: topCandidates,
  totalFound: topCandidates.length,
  bestLogo: topCandidates.length > 0 ? topCandidates[0] : null,
  url: baseUrl,
  needsAIAnalysis: shouldSendToAI(),
  aiPrompt: generateAIPrompt(),
  debug: debugInfo
};

